sys_script_include.script:
var DevUtils = Class.create();
DevUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		var today = (new GlideDateTime()).getLocalDate();
		var testdate = (new GlideDateTime()).getLocalDate();

		var comparison = today.compareTo(testdate);
		var isToday = (comparison == 0);

		if (isToday) {
			return true;
		}
		else {
			return false;
		}

	},

    type: 'DevUtils'
};
